.column {
  float: left;
  width: 45%;
  margin-left: 1rem;
}
/* Clear floats after the columns */
.row:after {
  content: "";
  display: table;
  clear: both;
}
#lightgallery > a > img:hover {
   transform: scale(1.15, 1.15);
   transition: 0.4s ease-in-out;
   cursor: pointer;
}

/* For some reason, icons are not loaded so I choose them myself */
.lg-actions .lg-next {
  content:"\1433"
}
.lg-actions .lg-prev {
  content:"\1438"
}
.lg-toolbar .lg-download {
 content:"\2913"
}
.lg-toolbar .lg-close {
 content:"\00D7"
}
.lg-toolbar .lg-icon {
    background-color: #000;
    border-color: #000;
    border-style: solid;
}









